items.default:
[
  {
    "name": "CPU Usage",
    "query": "select last(moving_avg(cpu_percent_across_hosts, 300)) where category = CLUSTER and clusterName = $CLUSTERNAME"
  },
  {
    "name": "Disk Utilization",
    "query": "select last(moving_avg(utilization_across_disks, 300)) where category = CLUSTER and clusterName = $CLUSTERNAME"
  },
  {
    "name": "Memory Utilization",
    "query": "select last(moving_avg(total_physical_memory_used_across_hosts / total_physical_memory_total_across_hosts * 100, 300)) where category = CLUSTER and clusterName = $CLUSTERNAME"
  },
  {
    "name": "Disk Read IO",
    "query": "select last(moving_avg(total_read_bytes_rate_across_disks, 300)) where category = CLUSTER and clusterName = $CLUSTERNAME"
  },
  {
    "name": "Disk Write IO",
    "query": "select last(moving_avg(total_write_bytes_rate_across_disks, 300)) where category = CLUSTER and clusterName = $CLUSTERNAME"
  },
  {
    "name": "Network Bytes Transmit",
    "query": "select last(moving_avg(total_bytes_transmit_rate_across_network_interfaces, 300)) where category = CLUSTER and clusterName = $CLUSTERNAME"
  },
  {
    "name": "Network Bytes Received",
    "query": "select last(moving_avg(total_bytes_receive_rate_across_network_interfaces, 300)) where category = CLUSTER and clusterName = $CLUSTERNAME"
  },
  {
    "name": "Capacity Total",
    "query": "select last(total_dfs_capacity_across_hdfss) where category = CLUSTER and clusterName = $CLUSTERNAME"
  },
  {
    "name": "Capacity Used (Absolute)",
    "query": "select last(total_dfs_capacity_used_across_hdfss + total_dfs_capacity_used_non_hdfs_across_hdfss) where category = CLUSTER and clusterName = $CLUSTERNAME"
  },
  {
    "name": "Capacity Used (Percentage)",
    "query": "select last((total_dfs_capacity_used_across_hdfss + total_dfs_capacity_used_non_hdfs_across_hdfss) / total_dfs_capacity_across_hdfss) * 100 where category = CLUSTER and clusterName = $CLUSTERNAME"
  },
  {
    "name": "Transceivers",
    "query": "select last(moving_avg(total_xceivers_across_datanodes, 300)) where category = CLUSTER and clusterName = $CLUSTERNAME"
  },
  {
    "name": "Read IO",
    "query": "select last(moving_avg(total_bytes_read_rate_across_datanodes, 300)) where category = CLUSTER and clusterName = $CLUSTERNAME"
  },
  {
    "name": "Write IO",
    "query": "select last(moving_avg(total_bytes_written_rate_across_datanodes, 300)) where category = CLUSTER and clusterName = $CLUSTERNAME"
  },
  {
    "name": "Regions",
    "query": "select last(total_regions_across_regionservers) where category = CLUSTER and clusterName = $CLUSTERNAME"
  },
  {
    "name": "Regions in Transition",
    "query": "select last(total_regions_in_transition_across_masters) where category = CLUSTER and clusterName = $CLUSTERNAME"
  },
  {
    "name": "Longest Region in Transition",
    "query": "select last(stats(regions_in_transition_longest_time_across_masters, max)) where category = CLUSTER and clusterName = $CLUSTERNAME"
  },
  {
    "name": "Read Request Rate",
    "query": "select last(moving_avg(total_read_requests_rate_across_regionservers, 300)) where category = CLUSTER and clusterName = $CLUSTERNAME"
  },
  {
    "name": "Write Request Rate",
    "query": "select last(moving_avg(total_write_requests_rate_across_regionservers, 300)) where category = CLUSTER and clusterName = $CLUSTERNAME"
  },
  {
    "name": "Compaction Queue",
    "query": "select last(moving_avg(total_compaction_queue_size_across_regionservers, 300)) where category = CLUSTER and clusterName = $CLUSTERNAME"
  },
  {
    "name": "Flush Queue",
    "query": "select last(moving_avg(total_flush_queue_size_across_regionservers, 300)) where category = CLUSTER and clusterName = $CLUSTERNAME"
  },
  {
    "name": "Completed Queries",
    "query": "select sum(total_num_queries_rate_across_impalads) where category=CLUSTER and clusterName = $CLUSTERNAME"
  },
  {
    "name": "Sessions",
    "query": "select last(moving_avg(total_thrift_server_beeswax_frontend_connections_in_use_across_impalads + total_thrift_server_hiveserver2_frontend_connections_in_use_across_impalads, 300)) where category = CLUSTER and clusterName = $CLUSTERNAME"
  },
  {
    "name": "Completed Applications",
    "query": "select sum(total_apps_completed_rate_across_yarn_pools) where category=CLUSTER and clusterName = $CLUSTERNAME"
  },
  {
    "name": "Failed Applications",
    "query": "select sum(total_apps_failed_rate_across_yarn_pools) where category=CLUSTER and clusterName = $CLUSTERNAME"
  },
  {
    "name": "Pending Containers",
    "query": "select last(moving_avg(total_pending_containers_across_yarn_pools, 300)) where category = CLUSTER and clusterName = $CLUSTERNAME"
  }
]

{
  "version": 1,
  "rules": [
    {
      "description": "Redact passwords from json files",
      "caseSensitive": false,
      "trigger": "password",
      "search": "\"password\"[ ]*:[ ]*\"[^\"]+\"",
      "replace": "\"password\": \"BUNDLE-REDACTED\""
    },
    {
      "description": "Redact password= and password:",
      "caseSensitive": false,
      "trigger": "password",
      "search": "password[:=][^ \"\\\\]+",
      "replace": "password=BUNDLE-REDACTED"
    },
    {
      "description": "Redact passwd= and passwd:",
      "caseSensitive": false,
      "trigger": "passwd",
      "search": "passwd[:=][^ \"\\\\]+",
      "replace": "passwd=BUNDLE-REDACTED"
    },
    {
      "description": "Redact pass= and pass:",
      "caseSensitive": false,
      "trigger": "pass",
      "search": "pass[:=][^ \"\\\\]+",
      "replace": "pass=BUNDLE-REDACTED"
